import React, { useState } from 'react';
import './ToDolist.css';
import ToDolist from './ToDolist';
import {db} from './fire';
import  firebase from 'firebase'




const App = () => {
  const [addItem , getItem ] = useState([]);
 const  [printItem , getPItem ] = useState([]);



  const addEvent = (pass) => {
   getItem(pass.target.value);
  }
  const PrintItemF = () => {
    // getPItem((prevval) => {
    //   return [...prevval , addItem ]
    // });
    getItem('')

    const todoRef = firebase.database().ref('task/')
    const add = {
      todo : addItem ,
      complete : false
    }
    todoRef.push(add)


  }

  const fetch = firebase.database().ref('task/')
  fetch.on('value' , (snapshot) => {
    const fetchToDo = snapshot.val();
    printItem.push(fetchToDo)

    getPItem(printItem)
  })



  const deleteFunc = (id) => {
    getPItem((delval) =>{
      return delval.filter((currdata , index) =>{
        return index !== id
      })
    })
    //console.log(id)
  } 
return (
  <div className="main-div">
    <div className="center-div">
      <h1>ToDo list</h1>
      <input 
       type="text" 
       placeholder="add an item" 
       onChange={addEvent} 
      // value={addItem}
       />
      <i class="fas fa-plus-circle" on onClick={PrintItemF}></i>
      <ol>
        {/* {  
           printItem.map((val , index) => {
           return (
             <ToDolist 
              key = {index}
              id = {index} 
              text = {val}
              onSelect = {deleteFunc} />)
           })

        } */}
        <li>{printItem}</li>
       </ol>

    </div>
  </div>
)


}
export default App;
