import React, { useState } from 'react';
import './ToDolist.css';
import ToDolist from './ToDolist';
import firebase from 'firebase';




const App = () => {
  const [addItem , getItem ] = useState([]);
 const  [printItem , getPItem ] = useState([]);

  const addEvent = (pass) => {
   getItem(pass.target.value);
  }
  const PrintItemF = () => {
    getPItem((prevval) => {
      return [...prevval , addItem ]
    });
    getItem('')

    const toDoRef = firebase.database().ref('todo')
    const ToDO = {
      addItem ,
      complete : true
    };
    toDoRef.push(ToDO)


  }


  const deleteFunc = (id) => {
    getPItem((delval) =>{
      return delval.filter((currdata , index) =>{
        return index !== id
      })
    })
    console.log(id)
  } 
return (
  <div className="main-div">
    <div className="center-div">
      <h1>ToDo list</h1>
      <input 
       type="text" 
       placeholder="add an item" 
       onChange={addEvent} 
       value={addItem}
       />
      <i class="fas fa-plus-circle" on onClick={PrintItemF}></i>
      <ol>
        {
           printItem.map((val , index) => {
           return (
             <ToDolist 
              key = {index}
              id = {index} 
              text = {val}
              onSelect = {deleteFunc} />)
           })
        }
       </ol>

    </div>
  </div>
)


}
export default App;
